mapper.select:
SELECT authority_code
  FROM AUTHORITY
 WHERE id = #{id}

mapper.delete:
UPDATE account
   SET isEnabled = 1
     , lastModDate = getDate()
     , lastModPerson = #{id}
 WHERE id = #{id}

mapper.insert:
INSERT INTO AUTHORITY
(      id
     , authority_code
) VALUES (
       #{id}
     , #{rols}
);

mapper.delete:
DELETE AUTHORITY
 WHERE id = #{id}
   AND authority_code = #{rols}
