mapper.insert:
insert into payment (
       account_no
     , card_no
     , card_name
     , expiration_month
     , expiration_year
     , card_verification_code
     , is_default
     , reg_date
     , reg_person
     , last_mod_date
     , last_mod_person
) values (
       #{accountNo}
     , #{cardNo}
     , #{cardName}
     , #{expirationMonth}
     , #{expirationYear}
     , #{cardVerificationCode}
     , #{isDefault}
     , getDate()
     , #{accountNo}
     , getDate()
     , #{accountNo}
)

mapper.update:
update payment
   set last_mod_date = getDate()
     , last_mod_person = #{accountNo}
     , is_default = #{isDefault}
 where account_no = #{accountNo}
   and payment_id = #{paymentId}

update payment
   set last_mod_date = getDate()
     , last_mod_person = #{accountNo}
     , is_default = 0
 where account_no = #{accountNo}

mapper.delete:
delete payment
 where account_no = #{accountNo}
   and payment_id = #{paymentId}
